def countWords1(speech1):
    a = {}
    for sentence in speech1:
        words=sentence.split()
        for word in words:
            if word in a:
                a[word]+=1
            else:
                a[word]=1
    return a

def getWordsFreqGreater1(b,a):
    c=list()
    for k in a:
        if a[k]>b:
            c.append(k)
    return c

wordDict1=countWords1(speech1)
freqWordsList1=getWordsFreqGreater1(2,wordDict1)

#단어 추출
def countWords2(speech2):
    x = {}
    for sentence in speech2:
        words=sentence.split()
        for word in words:
            if word in x:
                x[word]+=1
            else:
                x[word]=1
    return x

def getWordsFreqGreater2(y,x):
    z=list()
    for v in x:
        if x[v]>y:
            z.append(v)
    return z
wordDict2=countWords2(speech2)
freqWordsList2=getWordsFreqGreater2(2,wordDict2)
def compare():
    compare1=set(freqWordsList1)
    compare2=set(freqWordsList2)
    return compare1&compare2
def main():
    compare()
